import React from 'react'
import '../../sass/Definitions.scss'



export const SixThinkingHats = () => {
  return (
    <div className="def-wrapper">
      <h1>What is the <u>Six Thinking Hats</u> tool  ?</h1>
      <span><h1>Definition:</h1> The Six Thinking Hats is a creative thinking technique that helps individuals and teams to think more effectively and collaboratively.</span>
      <p>In module zero we already stated the problem so now we will do an activity to understand it better from different perspectives. </p>
    </div>
  )
}
